import { Component } from "@angular/core";
import { FormGroup, FormControl, Validators, FormBuilder, ReactiveFormsModule } from '@angular/forms';
import { Router } from '@angular/router';
import { HttpClient } from '@angular/common/http';
@Component({
  selector: "app-items-itemform",
  templateUrl: "./itemform.component.html"
})
export class ItemformComponent {
  itemForm: FormGroup;
  constructor(private _formbuilder: FormBuilder, private _router: Router, private _http: HttpClient) {
    this.itemForm = _formbuilder.group({
      category: ['', Validators.required],
      location: ['', Validators.required],
      rooms: [1, Validators.required],
      area: [0, Validators.required],
      renting: [0, Validators.required],
      description: [''],
      imageUrl: ['']
    });
  }
  onSubmit() {
    console.log("ItemCreate form submitted");
    console.log(this.itemForm);
    const newItem = this.itemForm.value;
    const createUrl = "api/item/create";
    this._http.post<any>(createUrl, newItem).subscribe(response => {
      if (response.success) {
        console.log(response.message);
        alert('Item created successfully');
        this._router.navigate(['/items']);
      } else {
        console.log('Item creation failed')
      }
    });
  
  }
  backToItems() {
    this._router.navigate(['/items']);
  }
}
<!DOCTYPE html>
<html>
<head>
    <meta charset="utf-8" />
    <title></title>
</head>
<body>
  <h2>Create A new Item</h2>
  <form [formGroup]="itemForm" (ngSubmit)="onSubmit()">
    <div class="form-group">
      <label for="category">Category:</label>
      <select type="text" id="category"
             formControlName="category" class="form-control bg-light">
        <option value="House">House</option>
        <option value="Dorm">Dorm</option>
        <option value="Apartment">Apartment</option>
        <option value="Semi-detached house">Semi-detached house</option>
        <option value="Cottage">Cottage</option>
        <option value="Townhouses">Townhouses</option>
        <option value="Other">Other</option>
      </select>
    </div>
    <div class="form-group">
      <label for="location">Location:</label>
      <input type="text" id="location"
             formControlName="location" class="form-control bg-light" />
    </div>
    <div class="form-group">
      <label for="rooms">Rooms:</label>
      <input type="number" id="rooms"
             formControlName="rooms" class="form-control bg-light " />
    </div>
    <div class="form-group">
      <label for="area">Area:</label>
      <input type="number" id="area"
             formControlName="area" class="form-control bg-light" />
    </div>
    <div class="form-group">
      <label for="renting">Renting:</label>
      <input type="number" id="renting"
             formControlName="renting" class="form-control bg-light" />
    </div>
    <div class="form-group">
      <label for="description">Description:</label>
      <input type="text" id="description"
             formControlName="description" class="form-control bg-light" />
    </div>
    <div class="form-group">
      <label for="imageUrl">ImageUrl:</label>
      <input type="text" id="imageUrl"
             formControlName="imageUrl" class="form-control bg-light" />
    </div>
    <button class="form-control btn btn-primary" type="submit" [disabled]="itemForm.invalid">Create Item</button>
    <button class="form-control btn-secundury" (click)="backToItems()" >Cancel</button>
  </form>

</body>
</html>
