<body>
  <div class="container mt-5">
    <div class="row justify-content-center">
      <div class="col-md-6">
        <div class="card">
          <div class="card-header">Login</div>
          <div class="card-body">
            <form [formGroup]="form" (ngSubmit)="onSubmit()">
              <div class="form-group">
                <label for="Email">Email</label>
                <input type="email" formControlName="Email" class="form-control" placeholder="Enter your email" required />
                <div *ngIf="f.Email.invalid && (f.Email.dirty || f.Email.touched)" class="text-danger">
                  <div *ngIf="f.Email.errors?.required">Email is required</div>
                  <div *ngIf="f.Email.errors?.email">Invalid email format</div>
                </div>
              </div>
              <div class="form-group">
                <label for="Password">Password</label>
                <input type="password" formControlName="Password" class="form-control" placeholder="Enter your password" required />
                <div *ngIf="f.Password.invalid && (f.Password.dirty || f.Password.touched)" class="text-danger">
                  <div *ngIf="f.Password.errors?.required">Password is required</div>
                </div>
              </div>
              <div class="form-group">
                <button [disabled]="loading" type="submit" class="btn btn-primary">Login</button>
                <div *ngIf="errorMessage" class="text-danger mt-2">{{ errorMessage }}</div>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  </div>
</body>
