import { Component, Injectable, NgModule } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { Router } from '@angular/router';
import { FormsModule } from '@angular/forms';

@NgModule({
  imports: [FormsModule],
  declarations: [RegisterComponent], // Declare your component here if it's not done elsewhere
})
@Injectable({
  providedIn: 'root'
})
@Component({
  selector: 'app-register',
  templateUrl: './register.component.html'
})
export class RegisterComponent {
  private baseUrl = 'api/user';
  user: any = {};
  loading = false;

  constructor(private http: HttpClient, private router: Router) { }

  async register(): Promise<void> {
    this.user.password = await this.hashPassword(this.user.password);
    const registerUrl = `${this.baseUrl}/register`;

    try {
      await this.http.post(registerUrl, {
        Name: this.user.Name,
        Email: this.user.Email,
        Phone: this.user.Phone,
        Password: this.user.password
      }).toPromise();

      // Registration successful, navigate to the home page
      this.router.navigate(['/home']);
    } catch (error) {
      console.error('Registration failed', error);
    }
  }

  login(credentials: { email: string, password: string }): Observable<any> {
    const loginUrl = `${this.baseUrl}/login`;
    return this.http.post(loginUrl, credentials);
  }

  private async hashPassword(password: string): Promise<string> {
    const encoder = new TextEncoder();
    const data = encoder.encode(password);
    const hashBuffer = await crypto.subtle.digest('SHA-256', data);
    const hashArray = Array.from(new Uint8Array(hashBuffer));
    const hashedPassword = hashArray.map(byte => byte.toString(16).padStart(2, '0')).join('');
    return hashedPassword;
  }
}
